var contact=document.querySelector(".contacts-button"),popup=document.querySelector(".modal-write-us"),close=popup.querySelector(".modal-close"),form=popup.querySelector(".write-us-form"),nameField=popup.querySelector("[name=name]"),emailField=popup.querySelector("[name=email]"),textField=popup.querySelector("[name=text]");contact.addEventListener("click",function(a){a.preventDefault();popup.classList.add("modal-show")});
close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-show");popup.classList.remove("modal-error")});form.addEventListener("submit",function(a){nameField.value&&emailField.value&&textField.value||(a.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))});
window.addEventListener("keydown",function(a){27===a.keyCode&&(a.preventDefault(),popup.classList.contains("popup-show")&&(popup.classList.remove("popup-show"),popup.classList.remove("popup-error")))});